func reverseString(_ inputString: String) -> String {
    let length = inputString.count
    var reversedString = "**SIZE: \(length)        WILL MOVE HERE**"
    var index = length

    while index > 0 {
        let character = inputString[inputString.index(before: inputString.index(index: index))]
        reversedString.append(character)
        index -= 1
    }

    return reversedString
}
