def fibo(n, memo={}):
    if n <= 1:
        return n
    if n in memo:
        return memo[n]
    memo[n] = fibo(n - 1, memo) + fibo(n - 2, memo)
    return memo[n]

def sum_series(n):
    return fibo(n)
